// Formik y Yup
declare module 'formik' {
  import { ComponentType, FormEvent, FormEventHandler, ReactNode } from 'react';
  
  export interface FormikErrors<Values> {
    [field: string]: string | string[] | FormikErrors<Values[keyof Values]> | undefined;
  }

  export interface FormikTouched<Values> {
    [field: string]: boolean | FormikTouched<Values[keyof Values]>;
  }

  export interface FormikState<Values> {
    values: Values;
    errors: FormikErrors<Values>;
    touched: FormikTouched<Values>;
    isSubmitting: boolean;
    isValidating: boolean;
    submitCount: number;
  }

  export interface FormikHelpers<Values> {
    setFieldValue(field: string, value: any, shouldValidate?: boolean): void;
    setFieldError(field: string, message: string): void;
    setFieldTouched(field: string, isTouched?: boolean, shouldValidate?: boolean): void;
    setErrors(errors: FormikErrors<Values>): void;
    setStatus(status: any): void;
    setSubmitting(isSubmitting: boolean): void;
    setTouched(touched: FormikTouched<Values>): void;
    setValues(values: Values): void;
    resetForm(nextState?: Partial<FormikState<Values>>): void;
    validateForm(values?: Values): Promise<FormikErrors<Values>>;
    validateField(field: string): Promise<void>;
    submitForm(): Promise<void>;
  }

  export interface FormikHandlers {
    handleSubmit: FormEventHandler<HTMLFormElement>;
    handleReset: FormEventHandler<HTMLFormElement>;
    handleBlur: FormEventHandler<any>;
    handleChange: FormEventHandler<any>;
  }

  export type FormikProps<Values> = FormikState<Values> & FormikHelpers<Values> & FormikHandlers;

  export interface FormikComputedProps<Values> {
    dirty: boolean;
    isValid: boolean;
    initialValues: Values;
    initialErrors: FormikErrors<Values>;
    initialTouched: FormikTouched<Values>;
    initialStatus: any;
  }

  export interface FormikBag<Props, Values> {
    props: Props;
    handleSubmit: (e?: FormEvent<HTMLFormElement>) => void;
    handleReset: (e?: FormEvent<HTMLFormElement>) => void;
    handleBlur: (e: any) => void;
    handleChange: (e: any) => void;
    setStatus: (status: any) => void;
    setErrors: (errors: FormikErrors<Values>) => void;
    setSubmitting: (isSubmitting: boolean) => void;
    setTouched: (touched: FormikTouched<Values>) => void;
    setValues: (values: Values) => void;
    resetForm: (nextValues?: Partial<FormikState<Values>>) => void;
    setFieldValue: (field: string, value: any, shouldValidate?: boolean) => void;
    setFieldError: (field: string, value: string) => void;
    setFieldTouched: (field: string, touched?: boolean, shouldValidate?: boolean) => void;
    validateForm: (values?: any) => Promise<FormikErrors<Values>>;
    validateField: (field: string) => void;
    submitForm: () => Promise<any>;
    initialValues: Values;
  }

  export interface FormikConfig<Values> {
    initialValues: Values;
    onSubmit: (values: Values, formikHelpers: FormikHelpers<Values>) => void | Promise<any>;
    validationSchema?: any;
    validate?: (values: Values) => void | object | Promise<FormikErrors<Values>>;
    onReset?: (values: Values, formikHelpers: FormikHelpers<Values>) => void;
    initialErrors?: FormikErrors<Values>;
    initialTouched?: FormikTouched<Values>;
    initialStatus?: any;
    enableReinitialize?: boolean;
    validateOnChange?: boolean;
    validateOnBlur?: boolean;
    validateOnMount?: boolean;
    isInitialValid?: boolean | ((props: any) => boolean);
    isValidating?: boolean;
    component?: React.ComponentType<FormikProps<Values>> | React.ReactNode;
    children?: ((props: FormikProps<Values>) => React.ReactNode) | React.ReactNode;
    render?: (props: FormikProps<Values>) => React.ReactNode;
  }

  export function useFormik<Values>(config: FormikConfig<Values>): FormikProps<Values>;

  export function Formik<Values = any, ExtraProps = {}>(props: FormikConfig<Values> & ExtraProps): JSX.Element;

  export interface FieldProps<Value = any> {
    field: {
      name: string;
      value: Value;
      onChange: FormEventHandler<any>;
      onBlur: FormEventHandler<any>;
    };
    form: FormikProps<any>;
    meta: {
      value: Value;
      error?: string;
      touched: boolean;
      initialValue?: Value;
    };
  }

  export function Field(props: any): JSX.Element;

  export function Form(props: any): JSX.Element;

  export function ErrorMessage(props: any): JSX.Element;
}

declare module 'yup' {
  export interface StringSchema {
    required(message?: string): StringSchema;
    min(limit: number, message?: string): StringSchema;
    max(limit: number, message?: string): StringSchema;
    email(message?: string): StringSchema;
    matches(regex: RegExp, message?: string): StringSchema;
    oneOf(values: any[], message?: string): StringSchema;
  }

  export interface NumberSchema {
    required(message?: string): NumberSchema;
    min(limit: number, message?: string): NumberSchema;
    max(limit: number, message?: string): NumberSchema;
    positive(message?: string): NumberSchema;
    negative(message?: string): NumberSchema;
    integer(message?: string): NumberSchema;
    oneOf(values: any[], message?: string): NumberSchema;
  }

  export interface BooleanSchema {
    required(message?: string): BooleanSchema;
    oneOf(values: any[], message?: string): BooleanSchema;
  }

  export interface DateSchema {
    required(message?: string): DateSchema;
    min(limit: Date | string, message?: string): DateSchema;
    max(limit: Date | string, message?: string): DateSchema;
    oneOf(values: any[], message?: string): DateSchema;
  }

  export interface ArraySchema {
    required(message?: string): ArraySchema;
    min(limit: number, message?: string): ArraySchema;
    max(limit: number, message?: string): ArraySchema;
    of(schema: any): ArraySchema;
  }

  export interface ObjectSchema {
    shape(shape: Record<string, any>): ObjectSchema;
    required(message?: string): ObjectSchema;
    noUnknown(allowUnknown?: boolean, message?: string): ObjectSchema;
    concat(schema: ObjectSchema): ObjectSchema;
  }

  export interface Schema {
    validate(value: any, options?: object): Promise<any>;
    validateSync(value: any, options?: object): any;
    isValid(value: any): Promise<boolean>;
    isValidSync(value: any): boolean;
  }

  export function string(): StringSchema;
  export function number(): NumberSchema;
  export function boolean(): BooleanSchema;
  export function date(): DateSchema;
  export function array(): ArraySchema;
  export function object(shape?: Record<string, any>): ObjectSchema;
  export function ref(path: string, options?: object): any;
}
